package com.blue.hospital.service.impl;import com.blue.hospital.dao.DrugstorageMapper;import com.blue.hospital.entity.Drugstorage;import com.blue.hospital.service.DrugStorageService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class DrugStorageServiceImpl implements DrugStorageService {    @Autowired    DrugstorageMapper drugstorageMapper;    public List<Drugstorage> getAllDrugStorage() {        List<Drugstorage> list = drugstorageMapper.getAll();        return list;    }    @Override    public int delDsById(Integer dsid) {        return drugstorageMapper.deleteByPrimaryKey(dsid);    }}