package com.blue.hospital.service.impl;import com.blue.hospital.dao.PatientMapper;import com.blue.hospital.entity.Patient;import com.blue.hospital.service.PatientService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class PatientServiceImpl implements PatientService {    @Autowired    PatientMapper patientMapper;    public Patient getinfoByid(Integer patientid) {        return patientMapper.selectByPrimaryKey(patientid);    }        public int updateinfo(Patient patient) {        return patientMapper.updateByPrimaryKeySelective(patient);    }        public int delinfo(Integer patientid) {        return patientMapper.deleteByPrimaryKey(patientid);    }        public int addinfo(Patient patient) {        return patientMapper.insertSelective(patient);    }    @Override    public List<Patient> getAllPatient() {        return patientMapper.getAllPatient();    }}